import React, {useState} from "react";
import { Button, Modal } from "react-bootstrap";

const IndividualNews = (props) => {

    const [view, setView] = useState(false);

    const viewDetailsHandler = () => {
         setView(true);
    }

    const closeDetailsHandler = () => {
        setView(false);
    }

  return (
    <>
      <div onClick={viewDetailsHandler} className="container shadow mt-5 p-5 ">{props.title}</div>
      {view && <div>
            <Button>Open modal</Button>
            <Modal show={true}>
                <Modal.Header>Modal head part</Modal.Header>
                <Modal.Body>Body of the modal here</Modal.Body>
                <Modal.Footer>
                    <Button onClick={closeDetailsHandler}>
                        Close Modal
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>}
    </>
  );
};

export default IndividualNews;
