import "./App.css";
import News from "./News/News";
import useFetch from "./useFetch";

function App() {
  const { data, loading, error } = useFetch(
    "http://hn.algolia.com/api/v1/search?query=test"
  );

  if (loading) {
    return <div>Loading</div>;
  }

  if (error) console.log(error);

  return (
    <>
      <div className="App">
        <div className="app-background">
        <nav className="top-nav navbar nav-expand navbar-dark bg-grey fixed-top">
          <div className="navbar-nav fs-6 text-white p-2 ">
            <h1>HACKER NEWS <i class="bi bi-code-square red-text"/></h1>
          </div>
        </nav>
        <h1 className="headlines mt-5 pt-5">TODAY'S HEADLINES</h1>
        <nav className="navbar navbar-expand-sm navbar-light bg-dark">

          <a href="#" className="navbar-brand mb-0 h1 text-light">
            Home
          </a>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item active">
                <a href="#" className="nav-link text-light">
                  Latest News
                </a>
              </li>
              <li className="nav-item active">
                <a href="#" className="nav-link text-light">
                  Sports Channel
                </a>
              </li>
              <li className="nav-item active">
                <a href="#" className="nav-link">
                  Pricing
                </a>
              </li>
            </ul>
          </div>
        </nav>

        <News data={data} />
        </div>
      </div>
    </>
  );
}

export default App;
