import React, { useState } from "react";
import { Button, Modal } from "react-bootstrap";
import './IndividualNews.css'

const IndividualNews = (props) => {
  const [view, setView] = useState(false);

  const viewDetailsHandler = () => {
    setView(true);
  };

  const closeDetailsHandler = () => {
    setView(false);
  };

  return (
    <>
      <div
        onClick={viewDetailsHandler}
        className="container container-headlines mt-5 p-5"
        style={{ cursor: "pointer" }}
      >
        {props.title}
      </div>
      {view && (
        <div>
          <Modal className="modal" show={true}>
            <Modal.Body className="modal__box">
            <a href={props.url}><Modal.Header>{props.title}</Modal.Header></a>
                <div>Points: {props.points}</div> 
                <div>Story Texts: {props.storyTexts}</div>
                <div>Comment Texts: {props.commentText}</div> 
                <div>Num Comments: {props.numComment}</div> 
                <Modal.Footer>
                <Button onClick={closeDetailsHandler}>Close</Button>
            </Modal.Footer>
            </Modal.Body>
          </Modal>
        </div>
      )}
    </>
  );
};

export default IndividualNews;
